mapper.select:
select * from orders


        and number = #{number}


        and phone = #{phone}


        and status = #{status}


        and user_id = #{userId}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select sum(amount) from orders


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and order_time = #{time}


        and status = #{status}

select count(*) from orders


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and status = #{status}


        and order_time = #{time}
